include "qelib1.inc";
qreg q[3];
creg c[3];
creg meas[3];
h q[0];
h q[1];
h q[2];
cx q[0],q[1];
rz(0.5) q[1];
cx q[0],q[1];
cx q[1],q[2];
rz(0.5) q[2];
cx q[1],q[2];
cx q[2],q[0];
rz(0.5) q[0];
cx q[2],q[0];
rx(0.6) q[0];
rx(0.6) q[1];
rx(0.6) q[2];
barrier q[0],q[1],q[2];
measure q[0] -> meas[0];
measure q[1] -> meas[1];
measure q[2] -> meas[2];

